import React, { useState } from "react";

function BookingForm(props) {
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");
  const [guests, setGuests] = useState("");
  const [occasion, setOccasion] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    props.submitForm(e);
  };
  const handleChange = (e) => {
    setDate(e);
    props.dispatch(e);
  };
  return (
    <header>
      <section className="">
        <form onSubmit={handleSubmit}>
          <fieldset>
            <label for htmlFor="book-date">
              Choose Date
            </label>
            <input
              id="book-date"
              value={date}
              onChange={(e) => handleChange(e.target.value)}
              type="date"
              required
            />
          </fieldset>
          <div>
            <label for htmlFor="book-time">
              Choose Time
            </label>
            <select id="book-time">
              <option value={time} onChange={(e) => setTime(e.target.value)}>
                Select a Time
              </option>
              {props.availableTimes.availableTimes.map((availableTime) => {
                return <option key={availableTime}>{availableTime}</option>;
              })}
            </select>
          </div>

          <div>
            <label htmlFor="book-guests">Number of guests:</label>
            <input
              id="book-guests"
              min="1"
              value={guests}
              onChange={(e) => setGuests(e.target.value)}
            />
          </div>
          <div>
            <label htmlFor="book-occasion">Occasion:</label>
            <select
              id="book-occasion"
              value={occasion}
                          onChange={(e) => setOccasion(e.target.value)}>
                          <option>Birth</option>
              </select>
          </div>
        </form>
      </section>
    </header>
  );
}

export default BookingForm;
